func threeSum(nums []int) [][]int {
	ret := make([][]int, 0)
	l := len(nums)

	sort.Ints(nums)
	if l < 3 {
		return ret
	}
	for i := 0; i < l-2; i++ {
		if i > 0 && nums[i-1] == nums[i] {
			continue
		}
		if nums[i] > 0 {
			return ret
		}
		for j, k := i+1, l-1; j < k; {
			s := nums[i] + nums[j] + nums[k]
			if s == 0 {
				ret = append(ret, []int{nums[i], nums[j], nums[k]})
				j++
				k--
				for j < k && nums[j-1] == nums[j] {
					j++
				}
				for j < k && nums[k] == nums[k+1] {
					k--
				}
			} else if s < 0 {
				j++
				for j < k && nums[j-1] == nums[j] {
					j++
				}
			} else {
				k--
				for j < k && nums[k] == nums[k+1] {
					k--
				}
			}
		}
	}
	return ret
}
