func findSubstring(s string, words []string) []int {
	if len(s) == 0 || len(words) == 0 {
		return nil
	}
	wordLen := len(words[0])
	for i := 1; i < len(words); i++ {
		if wordLen != len(words[i]) {
			return nil
		}
	}

	totalLen := wordLen * len(words)

	wordsMap := make(map[string]int, 0)

	for _, w := range words {
		wordsMap[w]++
	}

	ret := make([]int, 0)

	if len(words[0]) == 0 {
		for i := 0; i <= len(s); i++ {
			ret = append(ret, i)
		}
		return ret
	}

	for i := 0; i < wordLen; i++ {
		if totalLen+i > len(s) {
			break
		}
		stringMap := make(map[string]int, 0)
		for j := i; j < totalLen+i; j += wordLen {
			str := s[j : j+wordLen]
			stringMap[str]++
		}

		if diffMap(wordsMap, stringMap) {
			ret = append(ret, i)
		}

		for j := i; j+totalLen+wordLen <= len(s); j += wordLen {
			before := s[j : j+wordLen]
			after := s[j+totalLen : j+totalLen+wordLen]
			stringMap[before] = stringMap[before] - 1
			stringMap[after] = stringMap[after] + 1

			if diffMap(wordsMap, stringMap) {
				ret = append(ret, j+wordLen)
			}
		}

	}

	return ret
}

func diffMap(m1 map[string]int, m2 map[string]int) bool {
	for key, val1 := range m1 {
		if val2, exists := m2[key]; !exists || val1 != val2 {
			return false
		}
	}
	return true
}
